mapper.insert:
INSERT INTO boards (
    board_no
    , board_title
    , board_content
    , mem_id
    , board_date
    , del_yn
) VALUES (
    seq_boards.nextval
    , #{boardTitle}
    , #{boardContent}
    , #{memId}
    , SYSDATE
    , 'N'
)

mapper.update:
UPDATE boards 
SET board_title = #{boardTitle}
    ,board_content = #{boardContent}
WHERE board_no = #{boardNo}

UPDATE boards
SET del_yn = 'Y'
WHERE board_no = #{boardNo}
